  private void loadData() {
    // prepare geodatas
    // window with datas
    if (geoData != null) {
      geoData.close();
    }
    geoData = new DataLoader();
  }

  private void toggleVisible() {
    // close GUI, clear reference
    if (geoData.isOpen()) {
      geoData.setVisible(false);
    } else {
      geoData.setVisible(true);
      //geoData = null;
    }

  }

  private void exportData() {
    // start export to txt
    if (geoData != null) {
      geoData.startExport();
    } else {
      // no file
      MyUtilities.getErrorMsg("Fehler", "Keine Datei (*.shp) ausgew√§hlt!");
    }
  }

